widgets.script:
function pad(number) {
    if (number < 10) {
        return `0${number}`;
    }
    return number.toString();
}

function send_to_lcd() {
    set("lcd_mtc", `${pad(parseInt(locals.hours,16))}:${pad(parseInt(locals.minutes,16))}:${pad(parseInt(locals.seconds,16))}:${pad(parseInt(locals.frames,16))}`);
}
var mtc = value.split(" ");
if (mtc[0] == "f0") { // Full time
    locals.type = (parseInt(mtc[5],16) >> 5) & 0x3;
    locals.hours = parseInt(mtc[5],16) & 0x1f;
    locals.minutes = mtc[6];
    locals.seconds = mtc[7];
    locals.frames = mtc[8];
    if (mtc.toString() !== locals.originalText) {
        send_to_lcd();
        locals.originalText = mtc.toString();
    }
} else if (mtc[0] == "f1") {
    //console.log(mtc.toString());
    const quarterTime = parseInt(mtc[1],16);
    const type = (quarterTime >> 4) & 0x7;
    let nibble = quarterTime & 0x0f;
    let operator;
    if (type % 2 === 0) {
        // Low nibble
        operator = 0xf0;
    } else {
        // High nibble
        nibble <<= 4;
        operator = 0x0f;
    }
    //console.log("Operator: " + operator);
    switch (type) {
        case 0:
        case 1:
            locals.frames = parseInt(locals.frames & operator | nibble, 16);
            break;
        case 2:
        case 3:
            locals.seconds = parseInt(locals.seconds & operator | nibble, 16);
            break;
        case 4:
        case 5:
            locals.minutes = parseInt(locals.minutes & operator | nibble, 16);
            break;
        case 6:
            locals.hours = parseInt(locals.hours & operator | nibble, 16);
            break;
        case 7:
            locals.type = (nibble >> 5) & 0x3;
            nibble &= 0x10;
            locals.hours = parseInt(locals.hours & operator | nibble, 16);
            if (mtc.toString() !== locals.originalText) {
                send_to_lcd();
            }
            break;
        default:
            break;
    }
    //set("lcd_mtc", "Quarter Time Message: \n\n" + mtc)
} else if (mtc[0] == "f2") {
    set("lcd_mtc", "Song Position Message: \n\n" + mtc)
}

