:host{
  background-color: #808080;
}


@keyframes upndown {
    0% {
      transform: Scale(100%);
      filter: blur(0px);
    }
    65% {
      transform: Scale(125%);
      filter: blur(2.5px);
    }
    100% {
      transform: Scale(100%);
      filter: blur(0px);
    }
  }

@keyframes back {
    0% {
      transform: Scale(100%);
      filter: blur(0px) drop-shadow(2.5px 5px 0px #000000);
    }
    50% {
      transform: Scale(120%);
      filter: blur(2.5px);
    }
    65%{
      transform: Scale(80%);
      filter: blur(5px);
    }
    100%{
      transform: Scale(100%);
      filter: blur(0px) drop-shadow(2.5px 5px 5px #000000);
    }
  }


@keyframes upndown_Fast {
    0% {
      transform: Scale(100%);
    }
    65% {
      transform: Scale(125%);
    }
    100% {
      transform: Scale(100%);
    }
  }

@keyframes back_Fast {
    0% {
      transform: Scale(100%);
    }
    50% {
      transform: Scale(120%);
    }
    65%{
      transform: Scale(80%);
    }
    100%{
      transform: Scale(100%);
    }
  }


@keyframes Floating {
    25% {
      transform: Scale(75%);
    }
    50% {
      transform: Scale(135%);
    }
    100% {
      transform: Scale(100%);
    }
  }

@keyframes FloatingI {
    25% {
      transform: Scale(135%);
    }
    50% {
      transform: Scale(75%);
    }
    100% {
      transform: Scale(100%);
    }
  }


@keyframes Show {
    0% {
      transform: Scale(0%);
    }
    50% {
      transform: Scale(135%);
    }
    100% {
      transform: Scale(100%);
    }
  }

@keyframes hide {
    0% {
      transform: Scale(100%);
    }
    35% {
      transform: Scale(135%);
    }
    100% {
      transform: Scale(0%);
    }
  }

