content.touch:
// touch property
JS{
// little hack to define some locals only once 
// at compile time
locals.animating = false
locals.duration = 150 //ms
locals.framerate = 30 //fps
locals.y = 0
locals.touchValue = ()=>1 - locals.y
}


// store normalized coordinates
if (event.type == "start") {
  locals.y = event.offsetY / height
  locals.touchStart = Date.now()
  locals.animating = true
  setInterval(()=>{
    var anim = (Date.now() - locals.touchStart) / locals.duration
    if (anim > 1) {
      anim = 1
      clearInterval()
      locals.animating = false
    }
    var diff = locals.touchValue() - value
    set("this", value + diff * anim)
  },1000 / locals.framerate )

} else if (event.type == "move") {
  locals.y += event.movementY / height
  if (!locals.animating) set("this", locals.touchValue())
}
