widgets.css:
.frog {
  position: absolute;
  top:50%;left:50%;
  width: 50rem; height: 50rem;
  line-height: 50rem;
  margin-left: -25rem;
  margin-top: -25rem;
  background: green;
  transform: scale(VAR{scale, 1});
}

widgets.script:
if (locals.cursor === undefined){
  // init locals
  locals.cursor = 0 // animation state between 0 and 1
  locals.direction = 1 // -1 or 1
  locals.cycle = 1000 // animation duration in ms
  locals.framelength = 20 // frame duration in ms (20 = 50 fps)
}

if (value) {

  // start animation
  setInterval(()=>{

    // compute animation state
    locals.cursor += locals.direction * locals.framelength / locals.cycle

    if (locals.direction == 1 && locals.cursor >= 1) {
      // forward animation ended
      locals.direction = -1
      setVar('this', 'text', 'out')
    }
    else if (locals.direction == -1 && locals.cursor <= 0) {
      // backward animation ended
      locals.direction = 1
      setVar('this', 'text', 'in')
    }

    setVar('this', 'scale', 1 + locals.cursor)

  }, locals.framelength)


} else {

  // stop animation

  clearInterval()

}
