widgets.onValue:
setVar("this", "height", 0)
var lines = get("this").split(/\r|\r\n|\n/);
setVar("this", "height", (lines.length * getVar("this", "line-height") + 6));
setVar("cur_play_index", "height", getVar("this", "height"))
setVar("start_play_index", "height", getVar("this", "height"))
setVar("ledger_panel", "height", getVar("this", "height"))
setVar("ref_uid", "height", getVar("this", "height"))
var switchVals = lines.map((element, index) => element.trim().replace(',',''))
var switchIndices = lines.map((element, index) => index - 1)
set("ledger_switch_vals", switchVals)
set("ledger_switch_indices", switchIndices)

if(value === 1){
  send(false, "/transport", 1, get("start_play_index"));
} else {
  send(false, "/transport", 0, get("start_play_index"));
}

if(value === 0){
  var minSeed = 100000
  var maxSeed = 999999

  if(get("dur_seed_lock") === 0){
    set("dur_seed", Math.floor(Math.random() * (maxSeed - minSeed) + minSeed))
  }

  if(get("order_seed_lock") === 0){
    set("order_seed", Math.floor(Math.random() * (maxSeed - minSeed) + minSeed))
  }

  if(get("weights_seed_lock") === 0){
    set("weights_seed", Math.floor(Math.random() * (maxSeed - minSeed) + minSeed))
  }

  send('/generate', stateGet('root'), stateGet('ledger_panel'))

}
