content.css:
.fa-sawtooth {

  width: 20rem;

  height: 20rem;
  margin-left: auto;
  margin-right: auto;
  max-width: 100%;

  background: url( data:image/svg+xml;base64,);
}
.fa-triangle {

  width: 20rem;

  height: 20rem;
  margin-left: auto;
  margin-right: auto;
  max-width: 100%;

  background: url( data:image/svg+xml;base64,);
}

widgets.css:
.item:nth-child(1) {
background: @{EaselCompClr};
color: black;
font-size:100%;

}
.item:nth-child(2) {
background: @{EaselModClr};
color: black;
font-size:100%;
}

.item:nth-child(3) {
background: #5a6c99;
color: black;
font-size:100%;
}

:host {
  background: JS{{return @{this} == "0" ? "#5a6c99"
 : @{this} == "0.5" ? @{EaselModClr}
 : @{this} == "1" ? @{EaselCompClr} : ""


}};

border-radius:@{EaselBorderRadius};
