widgets.onCreate:
// internal variables
locals.durations = [];
locals.last = 0;
locals.index = -1;

// config variables
locals.n = 5;   // max numbers of taps to average
locals.min = 30;  // min bpm
locals.max = 600; // max bpm

widgets.onValue:
var lfoNum = getProp("parent", "variables").n
var now = Date.now();
locals.index += 1;

// reset if it's been too long since last tap
if (now - locals.last > 1000 * 60 / locals.min) {
  locals.durations = [];
  locals.index = -1;
  locals.last = now;
  return;
}

// store tap duration 
locals.durations[locals.index % locals.n] = now - locals.last;
locals.last = now;

// compute average bpm
var durations = locals.durations.filter(x => x > 0);
if (durations.length < 4) {
  //console.log('Not enough taps to calculate BPM');
  return;
}

var sum = durations.reduce((previous, current) => current += previous),
    avg = sum / durations.length,
    bpm = Math.round(60 / (avg / 1000));

// ensure bpm is within min and max bounds
if (bpm < locals.min || bpm > locals.max) {
  //console.log('BPM out of range: ' + bpm);
  return;
}


set(`variable_lfo${lfoNum}Tempo`, bpm);
set(`input_lfo${lfoNum}Tempo`, bpm);

setVar(`switch_lfo${lfoNum}Mode`, 'color', 'red')
