widgets.onValue:
var channels = get('this')
if (channels === null) { return; }
var hasRgb = channels.includes("red") && channels.includes("green") && channels.includes("blue") 
var hasRgb2 = channels.includes("red-2nd") && channels.includes("green-2nd") && channels.includes("blue-2nd") 
var hasIntensity = channels.includes("intensity")
var hasPanTilt = channels.includes("pan") && channels.includes("tilt")
var controls = []
// Setup avaialble control types
if ( hasRgb === true ) {
  controls.push('RGB')
}
if ( hasPanTilt === true ) {
  controls.push('XY')
}
if ( hasIntensity === true ) {
  controls.push('Knob')
}
// Always add RGB for macro ctrls
if ( controls.length === 0 ) {
  controls.push('RGB')
}
controls.push('Fader')
var currentCtrl = get('dropdownSelectCtrl')
set('variableSelectCtrls', controls)
if ( controls.includes(currentCtrl) ){
  set('dropdownSelectCtrl', currentCtrl)
}
else {
  // Automatic switch to a control type
  if ( hasRgb === true ) {
    set('dropdownSelectCtrl', 'RGB')
  } else if ( hasPanTilt === true ) {
    set('dropdownSelectCtrl', 'XY')
  } else if ( hasIntensity === true ) {
    set('dropdownSelectCtrl', 'Knob')
  }
}
// show 2nd rgb ctrl switch
if ( hasRgb2 === true ) {
  set('variable_show_rgb2', 1)
} else {
  set('variable_show_rgb2', 0)
}
// Check if actual selected channel exists, if not set first
var activeChannel = get('universalset1')
if ( channels.includes(activeChannel) === false ) {
  var channel = channels
  if ( Array.isArray(channels) === true ) {
    channel = channels[0]
  }
  updateProp('universalset1', 'values')
  setTimeout(function(){set('universalset1', channel)}, 100)
  //setTimeout(function(){send('/DMX/universal1', channel)}, 250)
}

