widgets.colorWidget:
JS{{
var color = 'auto'
var channelId = @{parent.variables.n}
var controlChannels = @{variable_control_channels}
var colors = @{variableObjectColors}

if ( colors != null ){
  if ( Array.isArray(colors) ){
    if ( colors.length > 0 ){
      color = colors[0]
    }
  }
  else { color = colors }
}

// For template or single channel objects return 'auto'
if ( channelId === 'template' || controlChannels === null ) { return 'auto' }
if (typeof controlChannels === 'string' || controlChannels instanceof String) { return 'auto' }

// Set color only for color channels (and 2nd color channels)
if ( Array.isArray(controlChannels) && channelId < controlChannels.length  ){
  var channelName = controlChannels[channelId]
  if ( channelName === 'red' || channelName === 'green' || channelName === 'blue' ){
    return color
  }
  if ( channelName === 'red-2nd' || channelName === 'green-2nd' || channelName === 'blue-2nd' ){
    if ( colors != null && Array.isArray(colors) ){
      if ( colors.length > 1 ){
        return colors[1]
      }
    }
  }
  if ( channelName === 'red-3rd' || channelName === 'green-3rd' || channelName === 'blue-3rd' ){
    if ( colors != null && Array.isArray(colors) ){
      if ( colors.length > 2 ){
        return colors[2]
      }
    }
  }
}
return 'auto'
}}

