/* Pour le menu, parent et enfants................................................................  */

JS{{
var couleurs = ["blue", "green", "red", "orange"]                            /* Défini les couleurs qui seront attribuées aux textes enfants........*/
var bkg = ["grey","purple","olive","black"]                                  /* Défini les couleurs qui seront utilisées pour les background enfants........*/
var index = (@{this}-1)                                                      /* Fais coincider l'index des couleurs avec la valeur du menu.......... */
locals.couleurs = couleurs[index];                                           /* Variables locales faisant le lien valeur du menu - index - couleur de texte */
locals.back= bkg[index];                                                     /* Variables locales faisant le lien valeur du menu - index - couleur de background */


/* CSS variable qui défini la couleur de texte affichée par le widget après sélection, et la taille de police................................... */
var css = ':host{color:#{locals.couleurs};font-size:180%;}'                  


/* Défini le css de chaque enfant avec sa couleur de texte et son background perso................................................................*/
for (var i = 0; i < couleurs.length; i++)                                  
              {css += ".item:nth-child(" + (i+1) + "){color:" + couleurs[i] + "; background:" + bkg[i] +"}\n"}         

return css
}}
