widgets.onCreate:
const vars = getProp("parent", "variables")

setVar("this", "isEnabled", false)

locals.start = function () {
  set('volFader_'+vars.n, 1)
  set('feedbackFader_'+vars.n, 1)
  set('inputVolFader_'+vars.n, 1)
}

locals.stop = function () {
  set('volFader_'+vars.n, 0)
  set('feedbackFader_'+vars.n, 0)
  set('inputVolFader_'+vars.n, 0)
}

widgets.onTouch:
if (event.type === 'start') {
  setVar("this", "isEnabled", true)
  if (getVar('clearButton', 'isEnabled')) {
    locals.stop()
  } else {
    locals.start()
  }
} else if (event.type === 'stop') {
  setVar("this", "isEnabled", false)
}

