widgets.onTouch:
if (event.type === 'start') {
  setVar('this', 'isEnabled', !getVar('this', 'isEnabled'))
  setVar('this', 'alphaFill', getVar('this', 'isEnabled') ? 1 : 0.15)
  locals.pressTime = Date.now()
} else if (event.type === 'stop') {
  if (Date.now() - locals.pressTime >= locals.longPressDelay && getVar('this', 'isEnabled')) {
     locals.disable()
  }
}
