widgets.script:
var increment = 0.05,
    timeBetweenIncrements = 2 // ms

if (value === 1) {
 setInterval(()=>{
  set("cam_" + get("cam_variable") + "_tilt", get("cam_" + get("cam_variable") + "_tilt") + increment)
 }, timeBetweenIncrements)
