widgets.onValue:
// apply limits
var x = Math.max(0, Math.min(1, value[1])),
    y = Math.max(0, Math.min(1, value[2]))

locals.queue.push([value[0],x,y])

// re-update widget value without retriggering script or sending message
set("this", [value[0],x,y], {sync: false, send: false})

widgets.onTouch:
// onTouch

// store normalized coordinates
if (event.type == "start") {
    locals.x = event.offsetX / width
    locals.y = event.offsetY / height
    value[0] = "moveto"
    value[1] = locals.x
    value[2] = locals.y

} else {
    // when the pointer is moving, increment coordinates
    // because offsetX and offsetY may not be relevant
    // if the pointer hovers a different widgets
    value[0]= "lineto"
    locals.x += event.movementX / width
    locals.y += event.movementY / height
    value[1] = locals.x
    value[2] = locals.y

}

// update widget value and send
set("this", value)

widgets.onDraw:
for (var i in locals.queue) {

  var data = locals.queue[i]

  if (data[0]=="clear")
  {
    ctx.beginPath()
    ctx.clearRect(0, 0, width,height)
    locals.queue = []
  }

  else if (data[0]=="moveto")
  {
      ctx.moveTo(data[1] * width, data[2]*height)
    // draw stroke
      ctx.stroke()

  }
  //else 
  else if (data[0]=="lineto")
  {
    ctx.lineTo(data[1] * width, data[2]*height)
  ctx.lineWidth = 15;
    // use colorFill property as stroke color
    ctx.strokeStyle = "rgba(200, 0, 0, 0.02)"
    // draw stroke
    ctx.stroke()
  }
}

locals.queue = []
