widgets.onValue:
var buttons = globals.monitorVariables.buttons
var faders = globals.monitorVariables.faders

// Function to set all buttons and faders to a specific value
globals.activateMonitor = function(value, faderID, buttonID, cc) {

    console.log(buttonID+'is using the function!');
    // DEACTIVATE
    if (value !== 1) {

        // Set all faders to 0
        faders.forEach(function(fader) {
          set(fader, 0);
        });

        // Send according CC to "mute" all track
        send("/control", 10, cc, 0);
        return;
    }


    // ACTIVATE
    // Turn on the activating button and set activating fader to 100
    set(faderID, 100);
    // Send according CC to activate the track
    send("/control", 10, cc, 100);

    // Turn off all other buttons
    buttons.forEach(function(button) {
        if (button !== buttonID) {
          set(button, 0, {script:false}, {sync:false});
        }
    });

    // Turn off all other faders and tracks
    faders.forEach(function(fader) {

        if (fader !== faderID) {
            // Set other faders to 0
            set(fader, 0);

            // Send according CC to "mute" other tracks
            send("/control", getProp(fader, 'preArgs')[0], getProp(fader, 'preArgs')[1], 0)
        }
    });
}

