widgets.css:
h1 
{

	background-size: 120%;                           /* la taille du dit background */
	color: grey;                              /* couleur du texte principal écrit en html */ 
	-moz-background-clip: text;                      /* les deux éléments qui viennent réalise le montage */
        -webkit-background-clip: text;
	text-transform: uppercase;                       /* uppercase donne des majuscules, lowercase des minuscules */
	font-size: 200%;                                 /* la taille de police d'écriture */
	line-height: 120%;                               /* l'espacement entre les lignes */
	margin: 15rem -20rem;                            /* Position du texte et position de l'arrière-plan, en px ou rem ou %... */
        position:relative;
}

h2
{
	background: purple;          /* le background derrière les lettres du texte */
	background-size: 120%;                           /* la taille du dit background */
	color: transparent;                              /* couleur du texte principal écrit en html */ 
	-moz-background-clip: text;                      /* les deux éléments qui viennent réalise le montage */
        -webkit-background-clip: text;
	font-size: 140%;                                 /* la taille de police d'écriture */
	line-height: 120%;                               /* l'espacement entre les lignes */
	margin: 10rem -20rem;                            /* Position du texte et position de l'arrière-plan, en px ou rem ou %... */
        position:relative;
}

widgets.script:
var fad = 'fader_9'

if (value == 1 && get(fad) != 500)

  {
  set(fad,0)
  locals.Up=setInterval(function(){set(fad, get(fad)+1)},10)
  }

else if (value == 1 &&  get(fad) == 500)

  {
  locals.Down=setInterval(()=>{set(fad, get(fad)-1)},10)
  }


else if (value === 0) 
{
  clearInterval(locals.Up); 
  clearInterval(locals.Down);
}

// REACTIVE LE BOUTON, Stoppe le process et RTZ, attribue texte.

var bouton = 'button_9'
var compteur = 'variable_compteur_4'
var FADER = get('this')


// RE-TOGGLE BUTTON............................................................................

if((FADER === 0 || FADER == 500) && (get(compteur) !==0 && get(compteur) < 66))   // Relance le script du bouton pour redescendre et remonter ...
{
  set(bouton,0);set(bouton,1)
}


// END PROCESS..................................................................................

if(FADER === 0 && get(compteur) == 66 )  // Remise à 0 de variable_compteur_4 en fin de process
{
  set(compteur,0)
  set(bouton,0);
}


// TEXT HTML.....................................................................................

if(locals.valeurprecedente !== undefined)                              // Si la valeur précédente est connue...
  {
  if(locals.valeurprecedente > value) {setVar(bouton,'text','Expire')}         // ... et qu'elle est supérieure à l'actuelle (donc que le fader redescend), écrit "Expire"
  else if(locals.valeurprecedente < value) {setVar(bouton,'text','Inspire')}   // ... mais si elle est inférieure à l'actuelle (donc que le fader remonte), écrit "Inspire"
  }


locals.valeurprecedente = value                                       // Mise à jour de la valeur. La variable vaut donc la valeur du fader...
