// startof startof startof function
// Function: resetBlink will reset the phase of the blinking animation
// Need to create a Variable widget called "resetCss"
// Need to parse (paste on "css" property)
// :host.on {
//     animation: mainBlink 1s infinite;
//     @{resetCss}
// }
globals.resetBlink = function resetBlink () {
  set('resetCss', 'animation:none')
  setTimeout(()=>{
  set('resetCss', '')
  })
}
// eof eof eof eof eof eof function


globals.apcMiniLed = function apcMiniLed(inputVelocity) {
    let outputArray = [];
    if ( inputVelocity < 7 && inputVelocity >= 0 ) {
        switch(inputVelocity){
            case 0:
                outputArray = [ '#f0f0f0' , 'paused' ];
            break;
            case 1:
                outputArray = [ '#00dd00' , 'paused' ];
            break;
            case 2:
                outputArray = [ '#00dd00' , 'running' ];
  set('resetCss', 'animation:none')
  setTimeout(()=>{
  set('resetCss', '')
  })
            break;
            case 3:
                outputArray = [ '#dd0000' , 'paused' ];
            break;
            case 4:
                outputArray = [ '#dd0000' , 'running' ];
  set('resetCss', 'animation:none')
  setTimeout(()=>{
  set('resetCss', '')
  })
            break;
            case 5:
                outputArray = [ '#ffcc00' , 'paused' ];
            break;
            case 6:
                outputArray = [ '#ffcc00' , 'running' ];
  set('resetCss', 'animation:none')
  setTimeout(()=>{
  set('resetCss', '')
  })

            break;
        }
    } else {
        outputArray = [ '#00dd00' , 'paused' ];
    }
    return outputArray;
}
