widgets.css:
.pips {
  position:absolute;
  top:0;left:0;right:0;bottom:0;
  pointer-events:none;
  margin:var(--widget-spacing);
}
.pips .v {
  position:absolute;
  top: 1rem;bottom:20rem;
  border-left:1rem solid grey;
  opacity:0.25;
}
.pips .v div {
  position:absolute;
  bottom:-26rem;
  left:-20rem;
  width:40rem;
  text-align:center;
}

.pips .h {
  position:absolute;
  left: 30rem;
  right: 1rem;
  border-top:1rem solid grey;
  opacity:0.25;
  padding-left:3rem;
  align-items: end;
  display:flex;
  line-height: 0rem;
  text-indent: -34rem;
}

widgets.onValue:
for (var i in locals.ids) {
  if (get(locals.ids[i]) != value[i]) {
    set(locals.ids[i], value[i])
  }
}
