widgets.onCreate:
// start with an empty queue of draw commands
locals.queue = [];
locals.shapes = [];
locals.playbackInterval = 50; // milliseconds between points

widgets.onTouch:
// onTouch
var nx = event.offsetX / width;
var ny = event.offsetY / height;

// log every event phase
var beforeLen = (locals.queue && locals.queue.length) || 0;
console.log("üîî onTouch: " + event.type + " (queue before: " + beforeLen + ")");

if (event.type === 'start') {
  locals.queue = [['moveto', nx, ny]];
  console.log("  ‚Üí start, queue reset");
}
else if (event.type === 'move') {
  locals.queue.push(['lineto', nx, ny]);
  console.log("  ‚Üí move, added [" +
    nx.toFixed(3) + ", " + ny.toFixed(3) +
    "] (queue: " + locals.queue.length + ")");
}
else if (event.type === 'stop') {
  locals.queue.push(['lineto', nx, ny]);
  console.log("  ‚Üí stop, final point [" +
    nx.toFixed(3) + ", " + ny.toFixed(3) +
    "] (total: " + locals.queue.length + ")");

  console.log("‚ñ∂Ô∏è Playing back " + locals.queue.length + " points (X‚Ääand‚Ääinverted Y ‚Üí [-1‚Ä¶1])");

  (function playPoint(i) {
    var pt = locals.queue[i];
    // X stays: [0‚Ä¶1]‚Üí[-1‚Ä¶1]
    var mx = pt[1]*2 - 1;
    // Y inverted: [0‚Ä¶1]‚Üí[+1‚Ä¶-1]
    var my = (1 - pt[2])*2 - 1;

    console.log(
      "   [" + (i+1) + "/" + locals.queue.length + "]" +
      " send('/source/9/x', " + mx.toFixed(3) + ")" +
      " send('/source/9/y', " + my.toFixed(3) + ")"
    );

    send('/source/9/x', mx);
    send('/source/9/y', my);

    if (i+1 < locals.queue.length) {
      setTimeout(function(){ playPoint(i+1); }, locals.playbackInterval);
    } else {
      console.log("‚úÖ Playback complete");
    }
  })(0);
}

widgets.onDraw:
ctx.save();
ctx.strokeStyle = 'chartreuse';     
ctx.lineWidth   = 2;
for (var i = 0; i < locals.queue.length; i++) {
  var cmd = locals.queue[i][0],
      x   = locals.queue[i][1] * width,
      y   = locals.queue[i][2] * height;
  if (cmd === 'moveto') {
    ctx.beginPath();
    ctx.moveTo(x, y);
  } else {
    ctx.lineTo(x, y);
    ctx.stroke();
  }
}
ctx.restore();
