  background:white
}
> inner > .navigation > .on {
  background:coral
}
> inner {
font-size: 1vmax;
font-family: Helvetica, "Trebuchet MS", Verdana, sans-serif;
}
@media screen and (min-width: 768px) {
:host {

    width: calc(50% - 100rem);

}

widgets.script:
const switchesArray = [
get("Lobby_Switch"),
get("Outdoors_Switch"),
get("Shop_Switch"),
get("Air_Lock_Switch"),
get("Wall_Switch"),
]

if (switchesArray.every(el => el == 0)) set("All_Lights_OnOff", 0, {sync: false, send: false})
if (switchesArray.some(el => el == 1)) set("All_Lights_OnOff", 1, {sync: false, send: false})
