widgets.touch:
// touch property

// store normalized coordinates
if (event.type == "start") {
    locals.x = event.offsetX / width
    locals.y = event.offsetY / height
} else if (event.type == "move") {
    // when the pointer is moving, increment coordinates
    // because offsetX and offsetY may not be relevant
    // if the pointer hovers a different widgets
    locals.x += event.movementX / width
    locals.y += event.movementY / height
}

locals.a = Math.atan2(locals.y - 0.5, locals.x - 0.5)
locals.r = Math.sqrt(Math.pow(locals.y - 0.5, 2)+ Math.pow(locals.x - 0.5, 2))

if (event.type == "stop") {
  set("this", [locals.a, locals.r])
}

widgets.draw:
// draw property

var r = Math.min(locals.r * 50, Math.min(width,height) / 2 - 10)

// draw circle at touch coordinates
ctx.arc(width / 2, height / 2, r, locals.a - Math.PI/8, locals.a + Math.PI/8)
// use colorFill property as stroke color
ctx.strokeStyle = cssVars.colorFill
ctx.lineWidth = cssVars.lineWidth
// draw stroke
ctx.stroke()
